!function(){var e=angular.module("Application",["MessageCenterModule"]);e.controller("FlashController",["$http","messageCenterService",function(e,s){e.get("/api/messages").success(function(e){e.length&&(e[0][0]=e[0][0].replace("error","danger"),s.add(e[0][0],e[0][1],{timeout:3e3}))})}]),e.controller("LoginController",["$http","messageCenterService",function(e,s){var o=this;o._login=function(){o.login&&o.password&&e.post("/api/login",{login:o.login,password:o.password}).success(function(e){if(e.success){var o=e.success.user;document.cookie="username="+JSON.stringify(o),window.location=e.success.homeUrl}else s.add("danger",e.errors[0])})}}])}();